with open('paper2016.txt', 'r') as f:
    text = f.read()

# Search for keywords related to society in the text
import re
society_words = set()
for line in text.split('\n'):
    # Look for lines containing 'society' or related terms
    if re.search(r'\bsociety\b', line, re.IGNORECASE):
        words = re.findall(r'\b\w+\b', line)
        for w in words:
            if 'society' in w.lower():
                society_words.add(w)

print(society_words)

# Search for all words in the document that might be labels from figures or axes
import re

labels = set()
for line in text.split('\n'):
    # Look for lines with words only (no numbers) and not too long
    if re.match(r'^[A-Za-z ,\-]+$', line) and len(line.split()) <= 5:
        for w in line.split(','):
            w_clean = w.strip().lower()
            if len(w_clean) > 2:
                labels.add(w_clean)

# Show some likely label words
sorted(labels)
